import { useState } from 'react';

export default function Brand() {
    const [brandName, setBrandName] = useState('');
    const [successMessage, setSuccessMessage] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    const handleSave = () => {
        // Validate input
        if (!brandName) {
            setErrorMessage('Brand name is required');
            return;
        }

        // Send data to backend
        fetch("http://localhost:8080/addBrand", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json',
            },
            body: JSON.stringify({ brand_name: brandName }), 
        })
        .then(response => {
            if (!response.ok) {
                throw new Error('Failed to save brand');
            }
            return response.json();
        })
        .then(data => {
            console.log('Brand saved successfully:', data);
            setSuccessMessage('Brand added successfully!');
            setBrandName(''); // Clear input field
        })
        .catch(error => {
            // console.error('Error saving brand:', error);
            setErrorMessage('Failed to save brand. Please try again later.');
        });
    };

    return (
        <div className="brand-container">
            <h1 className='brand-heading'>Add Brand</h1>
            <div className="form-group">
                <label htmlFor="brandName">Brand Name:</label>
                <input 
                    type="text" 
                    id="brandName" 
                    name="brandName"
                    value={brandName}
                    onChange={(e) => setBrandName(e.target.value)}
                />
                {errorMessage && <div className="error-msg">{errorMessage}</div>}
            </div>
            <div className="form-row">
                <button type="button" onClick={handleSave}>Submit</button>
                {successMessage && <p className="text-success">{successMessage}</p>}
            </div>
        </div>
    );
}
