import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const ShowProductDetails = () => {
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);

  useEffect(() => {
    const productData = localStorage.getItem('searchedProducts');
    if (productData) {
      setProduct(JSON.parse(productData));
    }
  }, []);

  const handleProceedToPayment = () => {
    navigate('/paymentprocess', { state: { product } });
  };

  if (!product) {
    return <div>Loading...</div>;
  }

  return (
    <div className="container mt-4">
      <h2 className="mb-4">Product Details</h2>
      <div>
        <h3>{product.modelName}</h3>
        <p>{product.description}</p>
        <p><strong>Base Price:</strong> {product.basePrice}</p>
        <p><strong>Brand:</strong> {product.brand ? product.brand.brand_name : 'Unknown'}</p>
        <button className="btn btn-primary" onClick={handleProceedToPayment}>
          Proceed to Payment
        </button>
      </div>
    </div>
  );
};

export default ShowProductDetails;
